<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of email_details * * @author Suharshana DsW */class EmailDetails {    public $id;    public $name;    public $datetime;    public $mobile_number;    public $email;    public $status;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `email_details` WHERE `id`=" . $id;            $db = new Database();            $result = mysql_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->name = $result['name'];            $this->datetime = $result['datetime'];            $this->mobile_number = $result['mobile_number'];            $this->email = $result['email'];            $this->status = $result['status'];            return $this;        }    }    public function create() {                date_default_timezone_set('Asia/Colombo');         $createdAt = date('Y-m-d H:i:s');                $query = "INSERT INTO `email_details` (`name`,`datetime`,`mobile_number`,`email`,`status`) VALUES  ('"                . $this->name . "','"                . $createdAt . "','"                . $this->mobile_number . "', '"                . $this->email . "', '"                . $this->status . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysql_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `email_details`ORDER BY datetime ASC ";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysql_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `email_details` SET "                . "`city` ='" . $this->city . "', "                . "`title` ='" . $this->title . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`short_description` ='" . $this->short_description . "', "                . "`description` ='" . $this->description . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        $query = 'DELETE FROM `email_details` WHERE id="' . $this->id . '"';         $db = new Database();         return $db->readQuery($query);    }    public function arrange($key, $img) {        $query = "UPDATE `email_details` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}